import React, { useEffect, useState } from 'react';
import { fetchComponents } from '../utils/api'; // Assuming you have an API call to fetch components
import ComponentCard from '../components/ComponentCard'; // Import the reusable component card

const HomePage = () => {
  const [components, setComponents] = useState([]);

  useEffect(() => {
    const loadComponents = async () => {
      const data = await fetchComponents(); // Fetch components from the API
      setComponents(data);
    };

    loadComponents();
  }, []);

  return (
    <div className="container mx-auto py-8">
      <h1 className="text-4xl font-bold mb-4">Welcome to Folonite.js Marketplace</h1>
      <p className="text-lg mb-4">
        Folonite.js is a flexible and powerful framework created to simplify component-based development with built-in support for Server-Side Rendering (SSR) and streaming.
      </p>
      <p className="text-lg mb-8">
        This marketplace serves as a hub for components, templates, and other features that can help you build your web applications faster and with more flexibility.
      </p>

      {/* Introduction sections */}
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6 mb-12">
        <div className="bg-white p-6 shadow-md rounded-md">
          <h2 className="text-2xl font-semibold mb-2">Components</h2>
          <p>Browse and install reusable components contributed by the community to speed up your development.</p>
        </div>
        <div className="bg-white p-6 shadow-md rounded-md">
          <h2 className="text-2xl font-semibold mb-2">Templates</h2>
          <p>Use pre-built website templates to kickstart your projects with ease.</p>
        </div>
        <div className="bg-white p-6 shadow-md rounded-md">
          <h2 className="text-2xl font-semibold mb-2">Get Involved</h2>
          <p>Folonite.js thrives on contributions from developers like you. Submit your own components and templates to help grow the ecosystem!</p>
        </div>
      </div>

      {/* Dynamically display components from the marketplace */}
      <h2 className="text-3xl font-bold my-8">Available Components</h2>
      {components.length === 0 ? (
        <p>No components available yet. Please check back later!</p>
      ) : (
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
          {components.map((component) => (
            <ComponentCard key={component.id} component={component} />
          ))}
        </div>
      )}
    </div>
  );
};

export default HomePage;
